	function NameToNum($imya, $dlina) {
		$bykvy = array (
			'A', 'a', 'J', 'j', 'S', 's', 'B', 'b', 'K', 'k', 'T', 't','C', 'c', 'L', 'l', 'U', 'u', 'D', 'd', 'M', 'm', 'V', 'v', 'E', 'e', 'N', 'n', 'W', 'w', 'F', 'f', 'O', 'o', 'X', 'x', 'G', 'g', 'P', 'p', 'Y', 'y', 'H', 'h', 'Q', 'q', 'Z', 'z', 'I', 'i', 'R', 'r',
		);
		$imya = translit($imya);
		for ($i = 0; $i < $dlina; $i++) {
			if (array_search($imya[$i], $bykvy)) {
				$namenum[] .= array_search($imya[$i], $bykvy);
			}
		}
		$oy = array_sum($namenum);
		return $oy;
	}
		
	function OneNum($sl) {
		$sl = (string)($sl);
		$sll = strlen($sl);
		$mynum = [];
		if ($sll != 1) {
			for ($i = 0; $i < $sll; $i++) {
				$mynum[] .= $sl[$i];
			}
			$wow = array_sum($mynum);
			
			return OneNum($wow);
		} else {
			$wow = $sl;
			return $wow;
		}
	}



	function translit($s) {
	  $s = (string) $s; // преобразуем в строковое значение
	  $s = strip_tags($s); // убираем HTML-теги
	  $s = str_replace(array("\n", "\r"), " ", $s); // убираем перевод каретки
	  $s = preg_replace("/\s+/", ' ', $s); // удаляем повторяющие пробелы
	  $s = trim($s); // убираем пробелы в начале и конце строки
	  $s = function_exists('mb_strtolower') ? mb_strtolower($s) : strtolower($s); // переводим строку в нижний регистр (иногда надо задать локаль)
	  $s = strtr($s, array('а'=>'a','б'=>'b','в'=>'v','г'=>'g','д'=>'d','е'=>'e','ё'=>'e','ж'=>'j','з'=>'z','и'=>'i','й'=>'y','к'=>'k','л'=>'l','м'=>'m','н'=>'n','о'=>'o','п'=>'p','р'=>'r','с'=>'s','т'=>'t','у'=>'u','ф'=>'f','х'=>'h','ц'=>'c','ч'=>'ch','ш'=>'sh','щ'=>'shch','ы'=>'y','э'=>'e','ю'=>'yu','я'=>'ya','ъ'=>'','ь'=>''));
	  $s = preg_replace("/[^0-9a-z-_ ]/i", "", $s); // очищаем строку от недопустимых символов
	  $s = str_replace(" ", "-", $s); // заменяем пробелы знаком минус
	  return $s; // возвращаем результат
	}

	function nextDate($the_day, $the_month, $the_year, $plus_day) {
		$triodin = array (1, 3, 5, 7, 8, 10, 12);
		$trinol = array (4, 6, 9, 11);
		$visok_year = array(2016, 2020, 2024, 2028, 2032, 2036);
		$next_date = [];
		$next_month = $the_month;
		$next_year = $the_year;
		for ($i = 1; $i <= $plus_day; $i++) {
			$the_day++;
			if (in_array($the_month, $triodin)) {
				if ($the_day > 31) {
					$the_day = 1;
					$next_month = $the_month + 1;
					if ($next_month > 12) {
						$next_month = 1;
						$next_year = $the_year + 1;
					}
				}
			}
			if (in_array($the_month, $trinol)) {
				if ($the_day > 30) {
					$the_day = 1;
					$next_month = $the_month + 1;
				}
			}
			if ($the_month == 2) {
				if (in_array($the_year, $visok_year)) {
					if ($the_day > 29) {
						$the_day = 1;
						$next_month = $the_month + 1;
					}
				} else {
					if ($the_day > 28) {
						$the_day = 1;
						$next_month = $the_month + 1;
					}
				}
			}
		}
		$next_date[0] = $the_day;
		$next_date[1] = $next_month;
		$next_date[2] = $next_year;
		return $next_date;
	}

